frappe.ui.form.on('Stock Entry', {
    validate: function (frm) {
        frm.doc.items.forEach(item => {
            if (item.item_code) {
                // Fetch the actual stock level for the selected item
                frappe.call({
                    method: 'frappe.client.get',
                    args: {
                        doctype: 'Bin',
                        filters: {
                            item_code: item.item_code,
                            warehouse: item.s_warehouse || item.t_warehouse
                        }
                    },
                    callback: function (response) {
                        if (response.message) {
                            const current_stock = response.message.actual_qty || 0;
                            const ten_percent_stock = current_stock * 0.10;

                            // Check if the entered quantity is below 10% of total stock
                            if (item.qty <= ten_percent_stock) {
                                frappe.msgprint({
                                    title: __('Low Stock Warning'),
                                    indicator: 'orange',
                                    message: __(
                                        `The quantity for item <b>${item.item_code}</b> is less than 10% of the available stock (${current_stock}). Please request more.`
                                    )
                                });
                            }
                        }
                    }
                });
            }
        });
    }
});

frappe.query_reports["Stock Projected Qty"] = {
    "formatter": function(value, row, column, data, default_formatter) {
        if (column.fieldname === "projected_qty" || column.fieldname === "actual_qty") {
            value = parseInt(value);  // Convert float to integer
        }
        return default_formatter(value, row, column, data);
    }
};

frappe.ui.form.on("Stock Entry", {
    validate: function(frm) {
        frm.doc.items.forEach(function(row) {
            if (row.qty) {
                let qty = parseFloat(row.qty); // Ensure number, handle decimal
                if (!isNaN(qty)) {
                    frappe.call({
                        method: "custome_app.utils.convert_number_to_words",
                        args: {
                            number: qty,  // This will now handle 7.5 correctly
                            lang: "ar"
                        },
                        callback: function(response) {
                            frappe.model.set_value(row.doctype, row.name, "qty_in_words", response.message || " ");
                        }
                    });
                }
            }
        });
    }
});

