frappe.ui.form.on("Stock Entry", {
    item_code: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];

        // Fetch the 'Requires Damaged Receipt' field from Item Doctype
        frappe.db.get_value("Item", row.item_code, "requires_damaged_receipt", (r) => {
            if (r.requires_damaged_receipt) {
                // Check available stock in Damaged Warehouse
                frappe.call({
                    method: "frappe.client.get_value",
                    args: {
                        doctype: "Bin",
                        filters: {
                            item_code: row.item_code,
                            warehouse: "Damaged Items Warehouse - اا"
                        },
                        fieldname: "actual_qty"
                    },
                    callback: function(response) {
                        let damaged_qty = response.message ? response.message.actual_qty : 0;

                        if (row.qty > damaged_qty) {
                            frappe.msgprint({
                                title: __("Insufficient Damaged Stock"),
                                message: __(`Cannot issue item <b>${row.item_code}</b> because only <b>${damaged_qty}</b> is available in 'Damaged Items Warehouse'.<br>
                                You need to receive at least <b>${row.qty - damaged_qty}</b> as 'Damaged Material' before proceeding.`),
                                indicator: "red"
                            });

                            // Clear the row to prevent incorrect selection
                            frappe.model.set_value(cdt, cdn, "item_code", "");
                        }
                    }
                });
            }
        });
    }
});

