frappe.ui.form.on("Stock Entry", {
    refresh: function (frm) {
        frm.add_custom_button(__('Synchronize Data'), function () {
            let file_input = $('<input type="file" accept=".sql">');
            file_input.trigger('click');

            file_input.on('change', function () {
                let file = file_input[0].files[0];
                if (file) {
                    let reader = new FileReader();
                    reader.readAsText(file);
                    reader.onload = function (e) {
                        frappe.call({
                            method: "custom_app.sync_data.restore_backup",
                            args: {
                                sql_content: e.target.result
                            },
                            callback: function (response) {
                                if (response.message === "success") {
                                    frappe.msgprint("Data synchronized successfully.");
                                } else {
                                    frappe.msgprint("Error during synchronization.");
                                }
                            }
                        });
                    };
                }
            });
        }).addClass("btn-primary");
    }
});

frappe.ui.form.on('Meal Schedule', {
    refresh(frm) {
        frm.add_custom_button('Calculate Ingredients', function() {
            frappe.call({
                method: 'meal_manage.api.get_ingredients_from_schedule',
                args: { schedule: frm.doc.name },
                callback: function(r) {
                    if (r.message) {
                        let msg = '<b>Required Ingredients:</b><br>';
                        r.message.forEach(i => {
                            msg += `${i.item_code}: ${i.qty} ${i.uom}<br>`;
                        });
                        frappe.msgprint(msg);
                    }
                }
            });
        });
    }
});

frappe.ui.form.on('Meal Schedule', {
    schedule_type(frm) {
        if (frm.doc.schedule_type === "Daily" && frm.doc.start_date) {
            frm.set_value('end_date', frm.doc.start_date);
        }
        if (frm.doc.schedule_type === "Weekly" && frm.doc.start_date) {
            let start = frappe.datetime.str_to_obj(frm.doc.start_date);
            let end = frappe.datetime.add_days(start, 6);
            frm.set_value('end_date', frappe.datetime.obj_to_str(end));
        }
    },
    start_date(frm) {
        if (frm.doc.schedule_type === "Daily") {
            frm.set_value('end_date', frm.doc.start_date);
        }
        if (frm.doc.schedule_type === "Weekly") {
            let start = frappe.datetime.str_to_obj(frm.doc.start_date);
            let end = frappe.datetime.add_days(start, 6);
            frm.set_value('end_date', frappe.datetime.obj_to_str(end));
        }
    }
});

