    refresh(frm) {
        frm.add_custom_button('Fetch from Meal Schedule', function() {
            frappe.prompt([
                {
                    'fieldname': 'meal_schedule',
                    'fieldtype': 'Link',
                    'label': 'Meal Schedule',
                    'options': 'Meal Schedule',
                    'reqd': 1
                }
            ],
            function(values){
                frappe.call({
                    method: 'meal_manage.api.get_ingredients_from_schedule',
                    args: { schedule: values.meal_schedule },
                    callback: function(r) {
                        if (r.message) {
                            frm.clear_table('items');  // clear existing rows
                            r.message.forEach(i => {
