                                row.item_code = i.item_code;  // ✅ exact match
                                row.qty = i.qty;
                                row.uom = i.uom;
                            });
                            frm.refresh_field('items');
                        }
                    }
                })
            }, 'Select Meal Schedule');
        });
    }
});

