frappe.ui.form.on('Stock Entry', {
    refresh(frm) {
        frm.add_custom_button('Calculate Ingredients', function() {
            calculate_ingredients(frm);
        });
    }
});

function calculate_ingredients(frm) {
    function run_calculation() {
        frappe.call({
            method: 'meal_manage.api.calculate_stock_entry_meals',
            args: { stock_entry: frm.doc.name },
            callback: function(r) {
                if (r.message) {
                    frm.clear_table('items');
                    r.message.forEach(i => {
                        frm.add_child("items", i);  // now contains full row data
                    });
                    frm.refresh_field("items");
                }
            }
        });
    }

    if (frm.is_dirty()) {
        frm.save().then(() => run_calculation());
    } else {
        run_calculation();
    }
}

frappe.ui.form.on('Meal Production Calculator', {
    refresh(frm) {
        frm.add_custom_button(__('Calculate Ingredients'), () => calculateIngredients(frm));
        frm.add_custom_button(__('Create Material Issue'), () => createMaterialIssue(frm));
    }
});

// Calculate Ingredients button
function calculateIngredients(frm) {
    if (!frm.doc.meal_to_produce || !frm.doc.number_of_people) {
        frappe.msgprint(__('Please select a Meal and enter Number of People.'));
        return;
    }

    // call backend calculation (could also use frappe.client.get on Meal Recipe)
    frappe.call({
        method: 'meal_manage.api.calculate_ingredients_for_meal',
        args: {
            meal_name: frm.doc.meal_to_produce,
            people: frm.doc.number_of_people,
            num_of_meals: frm.doc.num_week
        },
        callback: function(r) {
            if (r.message) {
                // clear and fill required_ingredients child table
                frm.clear_table('required_ingredients');
                r.message.forEach(i => {
                    let row = frm.add_child('required_ingredients');
                    row.item_code = i.item_code;
                    row.uom = i.uom;
                    // store both per-person (optional) and total
                    row.qty_per_person = null;
                    row.quantity = i.qty;
                });
                frm.refresh_field('required_ingredients');
                frappe.msgprint(__('Ingredient calculation complete.'));
            } else {
                frappe.msgprint(__('No ingredients found.'));
            }
        }
    });
}

// Create Material Issue button
function createMaterialIssue(frm) {
    if (!frm.doc.required_ingredients || frm.doc.required_ingredients.length === 0) {
        frappe.msgprint(__('Please calculate ingredients first.'));
        return;
    }

    // ensure the calculator is saved (so backend can read child table)
    if (frm.is_dirty()) {
        frm.save().then(() => call_create_se(frm));
    } else {
        call_create_se(frm);
    }
}

function call_create_se(frm) {
    frappe.call({
        method: 'meal_manage.api.create_stock_entry_from_calculator',
        args: {
            calc_name: frm.doc.name,
            from_warehouse: frm.doc.from_warehouse || null,
            custom_issue: frm.doc.custom_bentery_name_ 
        },
        callback: function(r) {
            if (r.message) {
                // open created Stock Entry
                frappe.set_route('Form', 'Stock Entry', r.message);
            } else {
                frappe.msgprint(__('Could not create Stock Entry.'));
            }
        }
    });
}
